package Whileloop;
import java.util.*;
public class Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		System.out.println(" Enter a numer  ");
		int n = sc.nextInt();
		int i=1;
		while(i<=n) {
			System.out.println(i);
			i++;
		}
	}

}
